import React, { useState, useContext } from "react";
import { Button, Stack, TextField } from "@mui/material";
import Botao from "../componentes/Botao";
import ValidacoesCadastro from "../contexts/ValidacoesCadastro";
import useErros from "../hooks/useErros";
import FieldText from "../componentes/TextField";

const Login = ({ aoEnviar }) => {
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const validacoes = useContext(ValidacoesCadastro);
  const [erros, validarCampos, possoEnviar] = useErros(validacoes);

  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        if (possoEnviar()) {
          aoEnviar({ email, senha });
        }
      }}
    >
      <h2>Digite seu usu√°rio e senha para acessar o MyImagesManager</h2>

        <FieldText
          value={email}
          onChange={(event) => {
            setEmail(event.target.value);
          }}
          onBlur={validarCampos}
          error={!erros.email.valido}
          helperText={erros.email.texto}
          id="email"
          label="Email"
          name="email"
          variant="outlined"
          margin="normal"
          fullWidth
        />

        <TextField
          value={senha}
          onChange={(event) => {
            setSenha(event.target.value);
          }}
          onBlur={validarCampos}
          error={!erros.senha.valido}
          helperText={erros.senha.texto}
          id="senha"
          name="senha"
          label="Digite sua senha"
          type="password"
          required
          variant="outlined"
          margin="normal"
          fullWidth
        />

      <Stack spacing={2} direction="row">
          <Botao
            type="submit"
            variant="contained"
            color="primary"
            children="Logar"
          />
          <Button
            style={{ backgroundColor: "#6278f7", padding: "10px 20px" }}
            href="http://localhost:3000/cadastro"
            type="submit"
            variant="contained"
            color="primary"
            children="Inscreva-se"
          />
        </Stack>
    </form>
  );
};

export default Login;
