import React, { useState, useContext } from "react";
import { Button, TextField } from "@mui/material";
import ValidacoesCadastro from "../contexts/ValidacoesCadastro";
import useErros from "../hooks/useErros";
import { useNavigate } from "react-router-dom";

const Cadastro = ({ aoEnviar }) => {
  const navigate = useNavigate()
  const [username, setUsername] = useState("");
  const [nomeCompleto, setNomeCompleto] = useState("");
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const [confirmarSenha, setConfirmarSenha] = useState("");
  const validacoes = useContext(ValidacoesCadastro);
  const [erros, validarCampos, possoEnviar] = useErros(validacoes);
  const [success, setSuccess] = useState();
  const [error, setError] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (senha !== confirmarSenha) {
      setError("As senhas devem ser iguais!");
    } else {
      const formData = new FormData();
      formData.append("username", username);
      formData.append("nomeCompleto", nomeCompleto);
      formData.append("email", email);
      formData.append("senha", senha);

      const headers = {
        headers: {
          "Content-Type": "application/json",
        },
      };
      await api
        .post("http://127.0.0.1:8000/users/", formData, headers)
        .then((response) => {
          if (response.status === 201) {
            setSuccess("Imagem salva com sucesso");
            setError("");
          }
        })
        .catch((event) => {
          setError(event.response.data["error"]);
          setSuccess("");
        });
      navigate("/login");
    }
  };

  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        if (possoEnviar()) {
          aoEnviar({ username, nomeCompleto, email, senha });
        }
      }}
    >
      <h2>Preencha os dados para criar um novo usuário</h2>
      <TextField
        value={username}
        onChange={(event) => {
          setUsername(event.target.value);
        }}
        onBlur={validarCampos}
        error={!erros.username.valido}
        helperText={erros.username.texto}
        id="username"
        label="Nome de Usuário"
        name="username"
        variant="outlined"
        margin="normal"
        fullWidth
      />

      <TextField
        value={nomeCompleto}
        onChange={(event) => {
          setNomeCompleto(event.target.value);
        }}
        onBlur={validarCampos}
        error={!erros.nomeCompleto.valido}
        helperText={erros.nomeCompleto.texto}
        id="nomeCompleto"
        label="Nome Completo"
        name="nomeCompleto"
        variant="outlined"
        margin="normal"
        fullWidth
      />

      <TextField
        value={email}
        onChange={(event) => {
          setEmail(event.target.value);
        }}
        onBlur={validarCampos}
        error={!erros.email.valido}
        helperText={erros.email.texto}
        id="email"
        label="Email"
        name="email"
        variant="outlined"
        margin="normal"
        fullWidth
      />

      <TextField
        value={senha}
        onChange={(event) => {
          setSenha(event.target.value);
        }}
        onBlur={validarCampos}
        error={!erros.senha.valido}
        helperText={erros.senha.texto}
        id="senha"
        label="Senha"
        type="password"
        name="senha"
        variant="outlined"
        margin="normal"
        fullWidth
      />
      <TextField
        value={confirmarSenha}
        onChange={(event) => {
          setConfirmarSenha(event.target.value);
        }}
        onBlur={validarCampos}
        error={!erros.confirmarSenha.valido}
        helperText={erros.confirmarSenha.texto}
        id="confirmarSenha"
        name="confirmarSenha"
        label="Confirme sua senha"
        type="password"
        variant="outlined"
        margin="normal"
        required
        fullWidth
      />
      <Button type="submit" variant="contained" size="large">
        Finalizar Cadastro
      </Button>
    </form>
  );
};

export default Cadastro;
