import React, { useEffect } from "react";
import { Button, TableBody, TableCell, TableRow, TextField } from "@mui/material";
import { Box, Container, Stack } from "@mui/system";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { api } from "../services/api";

const ImageUpload = () => {
  const navigate = useNavigate();

  const [image, setImage] = useState("");
  const [name, setName] = useState("");
  const [error, setError] = useState("");
  const [users, setUsers] = useState([]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("image", image);
    formData.append("name", name);

    const headers = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    await api
      .post("images/", formData, headers)
      .then((response) => {
        if (response.status === 201) {
        }
      })
      .catch((event) => {
        setError(event.response.data["error"]);
      });
    navigate("/");
  };

  useEffect(() => {
    async function loadUsers() {
      const response = await api.get("users/");
      console.log(response.data);
      setUsers(response.data);
    }
    loadUsers();
  }, []);

  return (
    <Container>
      <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
        <TextField
          value={image}
          onChange={(event) => {
            setImage(event.target.value);
          }}
          id="image"
          name="image"
          label="Digite o nome da sua imagem"
          type="image"
          variant="outlined"
          margin="normal"
          fullWidth
          required
        >
          Nome da Imagem
        </TextField>
        <TextField
          value={name}
          onChange={(event) => {
            setName(event.target.value);
          }}
          id="name"
          name="name"
          label="Digite o nome da sua imagem"
          type="name"
          variant="outlined"
          margin="normal"
          fullWidth
          required
        >
          Nome da Imagem
        </TextField>

        <Stack
          spacing={5}
          direction="row"
          alignItems="center"
          alignContent="center"
          justifyContent="center"
          justifyItems="center"
        >
          <TableBody>
            {users.map((row) => (<TableRow key={row.id}></TableRow>))}

          </TableBody>
          <Button size="large" variant="contained" component="label">
            Escolher Image
            <input hidden accept="image/*" multiple type="file"></input>
          </Button>
          <Button
            type="submit"
            size="large"
            variant="contained"
            component="label"
          >
            Enviar
          </Button>
        </Stack>
      </Box>
    </Container>
  );
};

export default ImageUpload;
