import React, { useEffect } from "react";
import {
  Button,
  FormControl,
  InputLabel,
  MenuItem,
  OutlinedInput,
  Select,
  TextField,
  useTheme,
} from "@mui/material";
import { Box, Container, Stack } from "@mui/system";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { api } from "../services/api";

const ImageUpload = () => {
  const navigate = useNavigate();

  const [users, setUsers] = useState([]);
  const [image, setImage] = useState("");
  const [nameImage, setNameImage] = useState("");
  const [error, setError] = useState("");
  const [nameUsersSelect, setNameUsersSelect] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("image", image);
    formData.append("name", nameImage);

    const headers = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    await api
      .post("images/", formData, headers)
      .then((response) => {
        if (response.status === 201) {
        }
      })
      .catch((response) => {
        console.log(response);
      });
    navigate("/");
  };

  useEffect(() => {
    async function loadUsers() {
      const response = await api.get("users/");
      //console.log(response.data.results);
      setUsers(response.data.results);
    }
    loadUsers();
  }, []);

  ///// Botão para escolher usuário

  const usuarios = [users];
  ////console.log([usuarios])

  const ITEM_HEIGHT = 48;
  const ITEM_PADDING_TOP = 8;
  const MenuProps = {
    PaperProps: {
      style: {
        maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
        width: 250,
      },
    },
  };

  const usersSelect = users?.map((item, index) => {
    return { value: item.nomeCompleto, label: item.nomeCompleto };
  });
  console.log("nome Usuarios", nameUsersSelect);

  /////

  return (
    <Container>
      <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
        <TextField
          value={nameImage}
          onChange={(event) => {
            setNameImage(event.target.value);
          }}
          id="nameImage"
          name="nameImage"
          label="Digite o nome da sua imagem"
          type="nameImage"
          variant="outlined"
          margin="normal"
          fullWidth
          required
        ></TextField>

        <TextField
          id="outlined-select-currency"
          select
          label="Usuário"
          value={nameUsersSelect}
          onChange={(event) => {setNameUsersSelect(event.target.value)}}
          helperText="Please select your currency"
        >
          {usersSelect.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>

        <Stack
          spacing={5}
          direction="row"
          alignItems="center"
          alignContent="center"
          justifyContent="center"
          justifyItems="center"
        >
          <TextField
            label="Escolha sua imagem"
            fullWidth
            required
            type="file"
            onChange={(event) => {
              setImage(event.target.files[0]);
            }}
          ></TextField>
          <Button
            type="submit"
            size="large"
            variant="contained"
            component="label"
          >
            Enviar
          </Button>
        </Stack>
      </Box>
    </Container>
  );
};

export default ImageUpload;
