import React, { useState, useContext } from "react";
import { Box, Button, Stack, TextField } from "@mui/material";
import Botao from "../componentes/Botao";
import ValidacoesCadastro from "../contexts/ValidacoesCadastro";
import useErros from "../hooks/useErros";
import { AuthContext } from "../contexts/auth";

const Login = ({ aoEnviar }) => {
  const { login } = useContext(AuthContext);
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const validacoes = useContext(ValidacoesCadastro);
  const [erros, validarCampos, possoEnviar] = useErros(validacoes);

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("submit", { email, senha });
    login(email, senha);
  };

  return (
    <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
      <h2>Digite seu usu√°rio e senha para acessar o MyImagesManager</h2>
      <Stack
        spacing={5}
        direction="row"
        alignItems="center"
        alignContent="center"
        justifyContent="center"
        justifyItems="center"
      >
        <TextField
          value={email}
          onChange={(event) => {
            setEmail(event.target.value);
          }}
          onBlur={validarCampos}
          error={!erros.email.valido}
          helperText={erros.email.texto}
          id="email"
          name="email"
          label="Email"
          type="email"
          variant="outlined"
          margin="normal"
          fullWidth
          required
        />

        <TextField
          value={senha}
          onChange={(event) => {
            setSenha(event.target.value);
          }}
          onBlur={validarCampos}
          error={!erros.senha.valido}
          helperText={erros.senha.texto}
          id="senha"
          name="senha"
          label="Digite sua senha"
          type="password"
          variant="outlined"
          margin="normal"
          required
          fullWidth
        />
      </Stack>
      <Stack spacing={5} direction="row">
        <Botao
          type="submit"
          variant="contained"
          color="primary"
          children="Logar"
        />
        <Button
          style={{ backgroundColor: "#6278f7", padding: "10px 20px" }}
          href="http://localhost:3000/cadastro"
          type="submit"
          variant="contained"
          color="primary"
          children="Inscreva-se"
        />
      </Stack>
    </Box>
  );
};

export default Login;
