import React, { useEffect } from "react";
import { Button, MenuItem, TextField } from "@mui/material";
import { Container, Stack } from "@mui/system";
import { useState } from "react";
import { api } from "../services/api";
import { useNavigate } from "react-router-dom";

const ImageUpload = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [image, setImage] = useState("");
  const [nameImage, setNameImage] = useState("");
  const [nameUsersSelect, setNameUsersSelect] = useState("");

  const handleSubmit = async (event) => {
    const formData = new FormData();
    formData.append("name", nameImage);
    formData.append("image", image);
    formData.append("user", users);
    ;

    const headers = {
      headers: {
        "Content-Type": "application/json",
      }
    }
      await api
        .post("users/", formData, headers)
        .then((response) => {
          if (response.status === 201) {

          }
        })
        .catch((event) => {

        });
      navigate("/");

    ;
  }
  useEffect(() => {
    async function loadUsers() {
      const response = await api.get("users/");
      setUsers(response.data.results);
    }
    loadUsers();
  }, []);

  const usersSelect = users?.map((item, index) => {
    return {
      value: item.id,
      label: item.nomeCompleto,
    };
  });

  console.log("Nome Usuarios", nameUsersSelect);

  return (
    <Container>
      <form
        component="form"
        onSubmit={(e) => {
          e.preventDefault();
          handleSubmit();
        }}
      >
        <TextField
          value={nameImage}
          onChange={(e) => {
            setNameImage(e.target.value);
          }}
          id="nameImage"
          name="nameImage"
          label="Digite o nome da sua imagem"
          type="nameImage"
          variant="outlined"
          margin="normal"
          fullWidth
          required
        ></TextField>

        <TextField
          variant="outlined"
          id="user"
          select
          fullWidth
          label="UsuÃ¡rio"
          value={nameUsersSelect}
          onChange={(e) => {
            setNameUsersSelect(e.target.value);
          }}
        >
          {usersSelect.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>

        <Stack
          spacing={5}
          direction="row"
          alignItems="center"
          alignContent="center"
          justifyContent="center"
          justifyItems="center"
        >
          <TextField
            fullWidth
            name="image"
            type="file"
            required
            onChange={(e) => {
              setImage(e.target.files[0]);
            }}
          ></TextField>
          <Button
            type="submit"
            size="large"
            variant="contained"
            component="label"
          >
            Enviar
          </Button>
        </Stack>
      </form>
    </Container>
  );
};

export default ImageUpload;
