import React from 'react';
import { Button, TextField } from "@mui/material";
import { Box, Container, Stack } from "@mui/system";
import SendIcon from '@mui/icons-material/Send';
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { api } from "../services/api";

const ImageUpload = () => {
  const navigate = useNavigate();

  const [image, setImage] = useState("");
  const [name, setName] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("image", image);
    formData.append("name", name);

    const headers = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    await api
      .post("users/", formData, headers)
      .then((response) => {
        if (response.status === 201) {
        }
      })
      .catch((event) => {
        setError(event.response.data["error"]);
      });
    navigate("/");
  };

  return (
    <Container>
      <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
        <Stack
          spacing={5}
          direction="row"
          alignItems="center"
          alignContent="center"
          justifyContent="center"
          justifyItems="center"
        >
          <TextField
            value={image}
            onChange={(event) => {
              setImage(event.target.value);
            }}
            id="image"
            name="image"
            label="Digite o nome da sua imagem"
            type="image"
            variant="outlined"
            margin="normal"
            fullWidth
            required
          >
            Nome da Imagem
          </TextField>
          <TextField
            value={name}
            onChange={(event) => {
              setName(event.target.value);
            }}
            id="name"
            name="name"
            label="Digite o nome da sua imagem"
            type="name"
            variant="outlined"
            margin="normal"
            fullWidth
            required
          >
            Nome da Imagem
          </TextField>
          <Button size="large" variant="contained" component="label">
            Imagem
            <input hidden accept="image/*" multiple type="file"></input>
          </Button>
          <Button  size="large" variant="contained" component="label" endIcon={<SendIcon />}>Enviar</Button>
        </Stack>
      </Box>
    </Container>
  );
};

export default ImageUpload;
